import React from 'react';
import Food from './Food';

class Plate extends React.Component{
  constructor(){
  super()
    this.state = {
      food:''
    };
    this.handleClick=this.handleClick.bind(this)
  }

  handleClick(e) {
    const {value, name} = e.target
    this.setState({
        [name]: value
    })
}

  render(){ 
    return (
      <Food {...this.state.food}/>
  );
}
}

export default Plate;
