import { useContext, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { AppContext } from './context/appContext';

export const ResultsProfile = () => {
    const {
        getTracksAnalysis,
        playlistTracks,
        found
    } = useContext(AppContext)

    let history = useHistory()

    const mappedTracks = playlistTracks.items && playlistTracks.items.map(item =>
        <>
            <p className='list-item list-decimal list-inside'> {item.track.name} </p>
        </>
        )

    // ** for testing **
    useEffect(() => {
        const gathered = playlistTracks.reduce((final, item) => {
            final.push(item)
            return [final]
        })
        console.log(gathered)
        // getTracksAnalysis(found)
    },[])
    // console.log(playlistTracks.items.map(item => 
    //     getTrackAnalysis(item.track.id)))

    return(
        <div className='container-main'>
            <button onClick={() => history.goBack()} className='btnbold-small bg-cerise-700 text-sm'> go back </button>
            <div className='container-main mb-0 h-screen bg-navy-700 mt-5 rounded'>
                <a className='text-4xl p-3' href={`https://open.spotify.com/playlist/${found.id}`}> {found.name} </a>
                {mappedTracks}
            </div>
        </div>
    )
}
