import React, { useState } from 'react';
import axios from 'axios';

export const UserContext = React.createContext();

const userAxios = axios.create();
userAxios.interceptors.request.use(config => {
    const token = localStorage.getItem('token')
    config.headers.Authorization = `Bearer ${token}`
    return config
});

export default function UserProvider(props){
    const initState = {
        user: JSON.parse(localStorage.getItem('user')) || null,
        token: localStorage.getItem('token') || '',
        lists: [],
        recentMood: [],
        friends: [],
        friendLists: [],
        friendPosts: [],
        errMsg: ''
    };

    const initSpotifyState = {
        spotifyUser: JSON.parse(localStorage.getItem('spotifyUser')) || null,
        spotifyToken: localStorage.getItem('spotify_access_token') || '',
        errMsg: ''
    };

    const [ spotifyUserState, setSpotifyUserState ] = useState(initSpotifyState);
    const [ userState, setUserState ] = useState(initState);

// for auth
    function signup(credentials){
        axios.post('/auth/signup', credentials)
        .then(res => {
            const { user, token } = res.data
            localStorage.setItem('token', token)
            localStorage.setItem('user', JSON.stringify(user))
            setUserState(prevUserState => ({
                ...prevUserState,
                user,
                token
            }))
        })
        .catch(err => handleAuthError(err))
    };

    function login(credentials){
        const parsedInputs = {
            username: credentials.username.split(' ').join('_'),
            password: credentials.password
        }
        axios.post('/auth/login', parsedInputs)
        .then(res => {
            const { user, token } = res.data
            localStorage.setItem('token', token)
            localStorage.setItem('user', JSON.stringify(user))
            setUserState(prevUserState => ({
                ...prevUserState,
                user,
                token
            }))
        })
        .catch(err => handleAuthError(err))
    };

    function logout(){
        setUserState({
            user: {},
            token: '',
        });
        localStorage.removeItem('token')
        localStorage.removeItem('user')
    };

//  err
    function handleAuthError(err){
        setUserState(prevState => ({
            ...prevState,
            errMsg: JSON.stringify(err.data)
        }))
    };

    function resetAuthError(){
        setUserState(prevState => ({
            ...prevState,
            errMsg: ''
        }))
    };

// share posts depending on type
    const shareItem = async (list, timeframe) => {
        if(list.type === 'playlist'){
            userAxios.post(`/app/lists`, list, {
                params: {
                    time: timeframe
                }
            })
            .then((res) => setUserState(prevState => ({...prevState, lists: [res.data]})))
            .catch(err => console.log(err))
        } else {
            userAxios.post(`/app/moods/${timeframe}`, list)
            .then((res) => setUserState(prevState => ({...prevState, recentMood: [res.data]})))
            .catch(err => console.log(err))
        }
    };

    // follow and unfollow
    const updateFollowStatus = (id, type) => {
        userAxios.post(`/app/users/friends`, {
            params: {
                type: type,
                id: id
            }
        })
        .then(res => 
            setUserState(prevState => ({
                ...prevState,
                user: res.data
            }))
            )
        .catch(err => console.log(err))
    };


// get all friends' posts in DB **
    const getStatus = async (type) => {
        if(type === 'user'){
        const { data } = await userAxios.get(`/app/moods`, {
            params: {
                type: type
            }
        })
        return data
        } else if(type === 'friends'){
        const { data } = await userAxios.get('/app/moods', {
            params: {
                type: type
            }
        })
        return data
    }};

// get recent playlist
    const getPosts = async (type) => {
        if(type === 'user'){
        const { data } = await userAxios.get(`/app/lists`, {
            params: {
                type: type,
            }
        })
        return data
    } else if(type === 'friends'){
        const { data } = await userAxios.get(`/app/lists`, {
            params: {
                type: type
            }
        })
        return data
    }};

    // delete account and logout
    const deleteUserAccount = () => {
        userAxios.delete(`/app/users/removeAcc`)
        .then(res => console.log(res.data))
        .catch(err => console.log(err))
        .finally(logout())
    };

    return(
        <UserContext.Provider
        value={{
            ...userState,
            setUserState,
            spotifyUserState,
            setSpotifyUserState,
            userState,
            signup,
            login,
            logout,
            userAxios,
            deleteUserAccount,
            shareItem,
            updateFollowStatus,
            getStatus,
            getPosts,
            resetAuthError
        }}>
            {props.children}
        </UserContext.Provider>
    )
};
