import { useContext, useEffect, useState } from 'react';
import { UserContext } from '../components/context/userProvider.js';
import { PostedMood } from '../components/postedMood.js';
import { PostedList } from '../components/postedList.js';

// friends' posts
export default function Lists(){
    const {
        getPosts,
        getStatus,
        setUserState,
        userState: {
            friendPosts
        },
        userState: {
            friendLists
        }
    } = useContext(UserContext);

    const [ toggleView, setToggleView ] = useState('')

    const mappedFriendsMoods = friendPosts && friendPosts.map(post => 
        <>
            <h3 className='text-sm text-indigo-500'> {post.userString}'s mood
                {post.timeline === 'short_term' && ` these past 30 days`}
                {post.timeline === 'medium_term' && ` these past 6 months`}
                {post.timeline === 'long_term' && ` the past year`} 
            </h3>
            <PostedMood
                key={post._id}
                id={post._id}
                items={post.items}
            />
        </>
    );

    const mappedFriendLists = friendLists && friendLists.map(list => 
        <PostedList
            list={list}
            key={list._id}
        />
        )

    useEffect(() => {
        getStatus('friends')
        .then(res => setUserState(prevState => ({
                ...prevState,
                friendPosts: res
            }))
        )
        getPosts('friends')
        .then(res => setUserState(prevState => ({
            ...prevState,
            friendLists: res
        })))
    },[])

    return(
        <div className='container-main'>
            <button className='btn bg-indigo-500' onClick={setToggleView(prevState)}> {} view </button>
            <h1 style={{color: 'gray'}}> friends' recent statuses </h1>
                {mappedFriendsMoods}
        </div>
    )
};
