import { useContext, useEffect } from 'react';
import { Link, Switch, Route } from 'react-router-dom';
import { UserContext } from '../components/context/userProvider.js';
import { PostedMood } from '../components/postedMood.js';
import { PostedList } from '../components/postedList.js';

// friends' posts
export default function Lists(){
    const {
        getPosts,
        getStatus,
        setUserState,
        userState: {
            friendPosts
        },
        userState: {
            friendLists
        }
    } = useContext(UserContext);

    const mappedFriendsMoods = friendPosts && friendPosts.map(post => 
        <>
            <h3 className='text-sm text-indigo-500'> {post.userString}'s mood
                {post.timeline === 'short_term' && ` these past 30 days`}
                {post.timeline === 'medium_term' && ` these past 6 months`}
                {post.timeline === 'long_term' && ` the past year`} 
            </h3>
            <PostedMood
                key={post._id}
                id={post._id}
                items={post.items}
            />
        </>
    );

    const mappedFriendLists = friendLists[0] && friendLists.map(list => 
        <PostedList
            list={list}
            key={list._id}
        />
        )

    useEffect(() => {
        getStatus('friends')
        .then(res => setUserState(prevState => ({
                ...prevState,
                friendPosts: res
            }))
        )
        getPosts('friends')
        .then(res => setUserState(prevState => ({
            ...prevState,
            friendLists: res
        })))
    },[])

    return(
        <div className='container-main'>
            friends
            <br/>
            <Link>
                <input type='button' value='moods' className='bg-cyan-200 text-cyan-800 btn'/>
            </Link>
            <Link>
                <input type='button' value='playlists' className='bg-cyan-200 text-cyan-800 btn'/>
            </Link>
            <Switch>
                    <Route 
                        path={`/friend_moods`}
                        render={() => 
                            <PostedMood/>
                    }>
                    </Route>
                    <Route
                        path={`/friend_lists`}
                        render={() => 
                            <PostedList/>
                    }>
                    </Route>
                </Switch>
        </div>
    )
};
