import { useContext, useState, useEffect } from 'react';
import { Switch, Route, Link, useRouteMatch } from 'react-router-dom';
// also doubles as profile view, inlcudes weekly list component
import { getCurrentUserTop } from '../components/spotify.js';
// includes user information from userContext
import { UserContext } from '../components/context/userProvider.js';


export default function Profile(){
    const {
        user,
        spotifyUser
    } = useContext(UserContext)

    const { url, path } = useRouteMatch();

    // const {

    // } = useContext(AppContext)
        const [ view, setView ] = useState('artists')
        const [ weeklyArtist, setWeeklyArtist ] = useState({})
        const [ weeklyTracks, setWeeklyTracks ] = useState({})
    // /pull from mongodb, saved lists
    const mappedLists = 'first option ternary; if lists? first here if not no lists yet'


    useEffect(() => {
        const fetchUserRecents = async () => {
            const { dataArtist } = await getCurrentUserTop('artists', 3, 'short_term')
            const { dataTracks } = await getCurrentUserTop('tracks', 3, 'short_term')
            setWeeklyArtist(dataArtist)
            setWeeklyTracks(dataTracks)
        }
        fetchUserRecents();
    },[])

    return(
        <div className="listprofileview-Container">
            {console.log(spotifyUser)}
            <select>
                {/* const mappedLists here */}
                <option> - no lists yet - </option>
            </select>
            <br/>
            <i style={{fontSize: '27px'}} className="fab fa-spotify"/> <h3> Spotify: { spotifyUser ? spotifyUser.display_name : 'error'} </h3>
            <h3> Username: @{user.username} </h3>
            <img src={spotifyUser.images[0].url} alt='spotify-user-img' />
            <div>
                <h1 style={{color: 'gray'}}> past week top listens (toggle by artist or track) </h1>
                <Link to={`${url}/recent_mood_artists`}> 
                artists
                    <input type='radio' name='viewType' value='artists' onClick={e => setView(e.target.value)} />
                </Link>
                <Link to={`${url}/recent_mood_tracks`}> 
                tracks
                <input type='radio' name='viewType'  value='tracks' onClick={e => setView(e.target.value)} />
                </Link> 
                <Switch>
                    <Route
                        path={`${path}/recent_mood_artists`}
                        component={RecentArtists}
                    />
                    <Route
                        path={`${path}/recent_mood_tracks`}
                        component={RecentTracks}
                    />
                    {/* <Route
                        path={`${path}/friends`}
                    /> */}
                </Switch>
            </div>
        </div>
    )
}
