                <Link> 

                tracks
                <input type='radio' name='viewType'  value='tracks' onClick={e => setView(e.target.value)} />
                </Link> 
                <Switch>
                    <Route
                        path={`${path}/artists`}
                    />
                    <Route
                        path={`${path}/tracks`}
                    />
                    <Route
                        path={`${path}/friends`}
                    />
                </Switch>
            </div>
        </div>
    )
}
