import { useContext } from 'react';

import { UserContext } from './context/userProvider.js';

export default function Banner() {
    const { 
        token,
        user,
        spotifyUser
    } = useContext(UserContext);
    // add transform animation that shrinks text to fit in upper left corner of screen
    // when change to profile view or search results return; allows enough space to 
    // view results etc


    return !spotifyUser ?
    <div className='bannerWrapper'>
        <img src="https://developer.spotify.com/assets/branding-guidelines/logo.png" alt="spotify-logo"/>
    </div>
    :
    <div className='grid grid-cols-4 border-solid border-5 border-red'>
        <span className='text-sm grid-cols-2'>
            <h1 className='font-sans text-2xl'> mood. </h1>
            <h3> Username: @{user.username} </h3>
        </span>
        <span className='text-xs grid-cols-2'>
            <img className='h-auto p-4 rounded-full ' src={spotifyUser.images[0].url} alt='spotify-user-img' />
            <i className='fab fa-spotify text-2xl'/> <h3> Spotify: { spotifyUser ? spotifyUser.display_name : 'error: cannot view username'} </h3>
        </span>
    </div>
}
