import React, { useState, useContext } from 'react';

import { AppContext } from '../context/appContext.js';

export const SearchBar = () => {
    const [ inputs, setInputs ] = useState('');
    const { searchUser } = useContext(AppContext);

    function handleSubmit(e){
        e.preventDefault()
        searchUser(inputs)
        setInputs('')
    }

    return(
        <div className=''>
            <form onSubmit={handleSubmit}>
                <input type='text' value={inputs} onChange={e => setInputs(e.target.value)} placeholder={`what's their username?`} className='p-1' required/>
                <input type='button' className='bg-cyan-200 text-cyan-800 rounded ' value='search'/>
            </form>
        </div>
    )
}
