import { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AppContext } from './context/appContext.js';

export const ResultDetails = props => {
    const {
        id,
        username,
        title,
        mood,
        item
    } = props

    const {
        getSelection
    } = useContext(AppContext)

    // useLocation and or useRef/Routematch? idk
    const location = props.username ? 'user' : 'results'

    return(
        <div onClick={() => getSelection(id, location)} className='bg-indigo-300 rou'>
            <Link to={`/${location}/${id}`}> 
                {username || title || item.name}
            </Link>
            {/* add ability to visit user profile if exists in DB 
            ternary, display spotify profile or mood. profile */}
            { item.owner && <p className='text-xs'> curated by {item.owner.display_name}</p> }
            { mood }
        </div>
    )
}
