import { useContext, useEffect } from 'react';
import { AppContext } from '../components/context/appContext.js';
import { UserContext } from '../components/context/userProvider.js';
import { PostedMood } from './components/postedMood.js';

// friends' lists
export default function Lists(){
    const {
        getStatus,
        userState: {
            friendPosts
        }
    } = useContext(UserContext);

    useEffect(() => {
        let type = 'friends'
        getStatus(type)
    },[]);

    const mappedFriendsMoods = friendPosts.map(friend => 
        <PostedMood
            key={friend.cue}
        />
    )

    return(
        <div className='container-main' >
            <h1 style={{color: 'gray'}}> Mood lists posted by friends </h1>
        </div>
    )
}
