import { useContext } from 'react';
import { Link, Switch, Route } from 'react-router-dom';

import { ResultDetails } from './resultDetails.js';
import { AppContext } from './context/appContext.js';

export const ResultComp = () => {
    const {
        found
        } = useContext(AppContext)

    // useLocation and or useRef/Routematch? idk
    const location = found.username ? 'user' : 'results'
    const id = found.username ? found.Id : found.Id

    const mappedResults = found && found.map(item =>
        <ResultDetails
            username={item.username}
            title={item.title && item.title}
            mood={item.mood && item.mood}
            userId={item._id}
        />
    )

    return(
        <div className='grid mt-2 bg-cyan-600'>
            <Link to={`/${location}/${id}`}> 
                {found.username || found.title}
            </Link>
            <Switch>
                <Route
                    path={`/user/:userId`}>
                    <UserProfile username={found.username} />
                </Route>
                <Route
                    path={`/results/:resultId`}
                    render={() => <SearchedItem/> }>
                </Route>
            </Switch>
            {mappedResults}
        </div> 
    )
}
