import { useContext, useEffect } from 'react';
import { Switch, Route, Link } from 'react-router-dom';
import { RecentTracks } from '../components/recentTracks.js';
import { RecentArtists } from '../components/recentArtists.js';
import { CheckMood } from '../components/checkMood.js';
import { UserContext } from '../components/context/userProvider.js';
import { PostedMood } from '../components/postedMood.js';
import { MoodItem } from '../components/moodItem.js';

export default function Profile(){
    const {
        getPosts,
        userState,
        getStatus,
        setUserState,
        deleteUserAccount
    } = useContext(UserContext);

    console.log(userState)

    const recentMapped = userState.recentMood && userState.recentMood.map(mood => 
        <>
            <h3 className='text-sm text-indigo-500'> {mood.userString}'s mood
                    {mood.timeline === 'short_term' && ` these past 30 days`}
                    {mood.timeline === 'medium_term' && ` these past 6 months`}
                    {mood.timeline === 'long_term' && ` the past year`} 
                </h3>
            <PostedMood 
                key={mood._id}
                id={mood._id}
                items={mood.items}
                />
        </>
    );

    const recentPlaylist = userState.lists[0] && userState.lists.map(list => 
        <div className='border-solid border-2 border-cerise-600'>
        {console.log(list)}
            <a className='text-sm text-indigo-500' href={list.ownerProfile} title='open user in Spotify'> {list.owner}</a>
            <a href={list.href} title='open playlist in Spotify'>
                {/* <img src={list.image} alt={list.name}/> */}
            </a>
        </div>
        )

    useEffect(() => {
        getStatus('user')
        .then(res => setUserState(prevState => ({
                ...prevState,
                recentMood: res
            }))
        )
        getPosts('user')
        .then(res => setUserState(prevState => ({
            ...prevState,
            lists: res
        })))
    },[])

    return(
        <div className='container-main'>
                <span className='text-sm' > set <span className='text-indigo-300'> mood </span> or view more detailed stats  </span>
                <Link to={'/check/moods'}>
                    <input className='bg-indigo-300 text-cyan-800 btn' type='button' value='mood view'/>
                </Link>
                {recentMapped}
                <p style={{color: 'gray'}} className='text-sm'> posted playlist </p>
                {recentPlaylist}
                <h1 className='text-sm pt-2' style={{color: 'gray'}}> click to view past month top </h1>
                <Link to={`/recent_mood_artists`}> 
                    <input className='bg-cyan-200 text-cyan-800 btn' type='button' value='artists'/>
                </Link>
                <Link to={`/recent_mood_tracks`}> 
                    <input className='bg-cyan-200 text-cyan-800 btn' type='button' value='tracks'/>
                </Link>
                <Switch>
                    <Route
                        path={`/check/moods`}
                        render={() => 
                            <CheckMood/>
                    }>
                    </Route>
                    <Route 
                        path={`/recent_mood_artists`}
                        render={() => 
                            <RecentArtists/>
                    }>
                    </Route>
                    <Route
                        path={`/recent_mood_tracks`}
                        render={() => 
                            <RecentTracks/>
                    }>
                    </Route>
                </Switch>
                <button className='btnbold-small bg-cerise-900' onClick={deleteUserAccount}> delete profile </button>
        </div>
    )
};
