import { useContext, useEffect } from 'react';
import { UserContext } from '../components/context/userProvider.js';
import { PostedMood } from '../components/postedMood.js';

// friends' lists
export default function Lists(){
    const {
        getStatus,
        userState: {
            friendPosts
        }
    } = useContext(UserContext);

    useEffect(() => {
        let type = 'friends'
        getStatus(type)
    },[]);

    const mappedFriendsMoods = friendPosts.map(post => 
        <div className='list-decimal p=10'>
            <PostedMood
                key={post._id}
                id={post._id}
                items={post.items}
            />
        </div>
    );

    return(
        <div className='container-main'>
            <h1 style={{color: 'gray'}}> Mood lists posted by friends </h1>
            <div className='list-inside list-decimal'>
                {mappedFriendsMoods}
            </div>
        </div>
    )
};
