import React, { useState, useContext, useEffect } from 'react';
import AuthForm from './components/forms/authForm.js';
import { UserContext } from './components/context/userProvider.js';
import { accessToken, getCurrentUserProfile } from './components/spotify.js';


export default function Auth(){
    const initInputs = {
        username: '',
        password: ''
    };

    const initState = {
        spotifyUser: JSON.parse(localStorage.getItem('spotifyUser')) || {},
        spotifyToken: localStorage.getItem('spotify_access_token') || null,
        errMsg: ''
    };

    const [inputs, setInputs] = useState(initInputs);
    const [toggle, setToggle] = useState(false);

    const [ spotifyUserState, setSpotifyUserState ] = useState(initState);

    const {
        signup,
        login,
        errMsg,
        resetAuthError
    } = useContext(UserContext);

    function handleChange(e){
        const {name, value} = e.target
        setInputs(prevInputs => ({
            ...prevInputs,
            [name]: value
        }));
    };

    function handleSignup(e){
        e.preventDefault()
        signup(inputs)
    };

    function handleLogin(e){
        e.preventDefault()
        login(inputs)
    };

    function toggleForm(){
        setToggle(prev => !prev)
        resetAuthError()
        setInputs(initInputs)
    };

    const {
        logout
    } = useContext(UserContext);


    useEffect(()=> {
        if(accessToken){
        // ** BETA get token from userState
            // setSpotifyToken(accessToken);
            localStorage.setItem('spotify_access_token', accessToken)
            setSpotifyUserState(prevState => ({
                ...prevState,
                spotifyToken: accessToken
            }))
            const fetchData = async () => {
            try {
                const { data } = await getCurrentUserProfile();
                // setSpotifyProfile(data);
                // where user information should be sent to back for use in model
                // will need to reapply email each time on login
                // just post to signup model route for new users
                localStorage.setItem('spotifyUser', JSON.stringify(data))
                setSpotifyUserState(prevState => ({
                    ...prevState,
                    spotifyUser: data
                }))
            } catch(err) {
                // ** send error to auth handler
                console.error(err);
            }}
            fetchData();
        // **** ***** *****
        // } else if (!accessToken & spotifyUserState.spotifyToken){
        //     return;
        // } else if (!accessToken & !spotifyUserState.spotifyToken){
        //     logout();
        // ****
        }
    }, [spotifyUserState.spotifyToken, logout]);

    return spotifyUserState.spotifyToken ? 
        <div className='authContainer'>
                { !toggle ?
                    <>
                        <h1 className='header'> Create Account </h1>
                            <AuthForm
                                handleChange={handleChange}
                                handleSubmit={handleSignup}
                                inputs={inputs}
                                btnText='create'
                                errMsg={errMsg}
                            />
                        <p style={{cursor: 'pointer'}} onClick={toggleForm}> I have an account! </p>
                    </>
                    :
                    <>
                        <h1 className='header'> Login </h1>
                            <AuthForm
                                handleChange={handleChange}
                                handleSubmit={handleLogin}
                                inputs={inputs}
                                btnText='login'
                                errMsg={errMsg}
                            />
                        <p style={{cursor: 'pointer'}} onClick={toggleForm}> I'm not a member! </p>
                    </>
                }
        </div>
        :
        <div>
            <img src="https://developer.spotify.com/assets/branding-guidelines/logo.png" alt="spotify-logo"/>
            <a href="http://localhost:8888/login"> Login with Spotify </a>
        </div>
};
