export default function AuthForm(props){
    const {
        handleChange,
        handleSubmit,
        btnText,
        errMsg,
        inputs: {
            username,
            password
        }
    } = props

    return(
    <form onSubmit={handleSubmit}>
        <input
            value={username}
            name='username'
            onChange={handleChange}
            placeholder='username'
            pattern='[A-Za-z0-9]'
        />
        <input
            type='password'
            value={password}
            name='password'
            onChange={handleChange}
            placeholder='password'
        />
        <button> {btnText} </button>
        <p > {errMsg} </p>
    </form>
    )

}
